		<div id="test1"></div>

	</body>
	<script type="text/babel">
		// 组合组件

		// 用函数的方式自定义组件
		// 自定义组件可以实现交互传参
		function HelloMsg(props){
			return <h1>hello~ {props.name} !</h1>
		}
